func checkDatabse(ctx context.Context) {
	err := di.InitDB().Ping(ctx)
	if err != nil {
		log.Panic("faild to connnect to database")
	}
	dataSource := di.NewDBDataSource()
	err = dataSource.CreateTables()
	if err != nil {
		log.Panic("faild to migrate database")
	}
}

