func startConsumming(ctx context.Context) {
	trxConsumer := di.RawTransactionHandler()

	// fmt.Println(reflect.TypeOf(trxConsumer))

	// trxConsumer.RawTransactionConsumer.Consumer()
	rabbit.RegisterConsumer[models.Tx]("rawTransaction", rawTransactionConsumer)
	// kafka.RegisterConsumer[models.Tx]("rawTransaction", rawTransactionConsumer)
	// rest.RegisterController[models.Tx]("rawTransaction", rawTransactionConsumer)
	// gopg.addRepository[models.Tx](t)
	go trxConsumer.Consumer(ctx, trxConsumer.Msg, trxConsumer.ChErr)
	go trxConsumer.Parser()

}

func startGrpcServer() {
	err := grpc.GetInstance().Start()
	if err != nil {
		log.Println("faild to stert GRPC server")
	}
}

func stopGrpcServer() {
	grpc.GetInstance().Stop()
}
